function $(q) {
    if (!q || q == "") return document.body
    else return document.querySelector(q)
}
function $$(q) {
    if (q != "" && q != null) return document.querySelectorAll(q)
}
function $parseURLArgs() {
    let str = window.location.toString().split("?")[1]
    try {
        let parameters = {}
        str.split("&").forEach(thing => { try { eval(`parameters.${thing.split("=")[0]}='${thing.split("=")[1]}'`) } catch {} })
        return parameters
    } catch(e) { return console.error(e) }
}